import {React, useState} from 'react'
import "./Dropdown.css"
import { serviceDropdown } from '../Navbar/NavItems'
import { Link } from 'react-router-dom'

const Dropdown = () => {
    const [Dropdown, setDropdown] = useState(false)
  return (
    <div>
        <ul className={Dropdown ? "dropdown-list clicked" : "dropdown-list"} onClick={() => setDropdown(!Dropdown)}>
            {serviceDropdown.map(item => {
                return (
                    <li key={item.id}>
                        <Link to={item.path} className={item.cName} onClick={() => setDropdown(false)}>{item.title}</Link>
                    </li>
                );
            })}
        </ul>
    </div>
  )
}

export default Dropdown
